mapper.insert:
INSERT INTO board (
	title, content, regdate, userno, readcount,filename_org,filename_real
) values(
	#{title},#{content},NOW(),#{userno},#{readcount},#{filename_org},#{filename_real}
	)

mapper.update:
UPDATE board SET 
	title = #{title}
	, content=#{content}

	, filename_org=#{filename_org}
	, filename_real=#{filename_real}

WHERE boardno=#{boardno}
